mapper.insert:

	INSERT INTO tb_act100 (GDS_CD,ACT_SEQ,MB_NO,BID_PRICE,BID_TIME,ACT_BID_STS_CD)
	SELECT #{gdsCd} 
		,(SELECT
			  LPAD(COALESCE(A.CNT,'0'),5,'0') AS ACT_SEQ
			FROM
			(
				SELECT 
					COUNT(*)+1 AS CNT
				FROM TB_ACT100 A
				WHERE A.GDS_CD = #{gdsCd}
			) A
		) AS ACT_SEQ
		,MB_NO
		,#{bidPrice}
		,#{bidTime}
		,'2'
	FROM tb_user100
	where USER_ID=#{userId};

mapper.update:
update tb_gds300
set ACT_STS_CD='3'
where GDS_CD=#{gdsCd}

mapper.select:
SELECT REMN_DEPOSIT
FROM tb_mb100
WHERE MB_NO=#{mbNo}
