mapper.select:
select login_sts_cd
  from tb_user100
 where user_id = #{userId}

mapper.update:
UPDATE TB_USER100
   SET PW_FAIL_CNT 	= #{pwFailCnt}
     , CHG_DT 		= NOW()
 WHERE USER_ID 		= #{userId}

UPDATE TB_USER100
   SET SESSION_KEY 	= #{sessionKey}
     , SESSION_DT	= #{sessionDt}
 WHERE USER_ID		= #{userId}
