mapper.delete:
  		DELETE 
  		FROM 	TB_CART100
		WHERE 	MB_NO = #{mbNo}
		AND		GDS_CD=#{gdsCd}
		AND		CART_SEQ=#{cartSeq}

mapper.update:
UPDATE 	TB_CALL100 
SET		CALL_STS_CD='2' 
WHERE	USED_GDS_NO=#{usedGdsNo}
AND 	MB_NO = #{mbNo}

mapper.select:
SELECT  REMN_DEPOSIT 
FROM 	TB_MB100 
WHERE 	MB_NO=#{mbNo}

  		SELECT FN_NM('GDS_NM',GDS_CD) AS gdsNm, 
		BID_TIME,BID_PRICE
		FROM TB_ACT100 WHERE MB_NO=#{mbNo};

SELECT PW 
FROM TB_USER100 
WHERE USER_ID=#{userId}
