mapper.select:
SELECT DISTINCT #{gdsSclassCd} AS GUBUN_CD /* 전체메뉴 구분코드 */ ,CASE WHEN #{gdsSclassCd} >= '10' AND #{gdsSclassCd} < '20' THEN '10' WHEN #{gdsSclassCd} >= '20' AND #{gdsSclassCd} < '30' THEN '20' WHEN #{gdsSclassCd} >= '30' AND #{gdsSclassCd} < '40' THEN '30' WHEN #{gdsSclassCd} >= '40' AND #{gdsSclassCd} < '50' THEN '40' WHEN #{gdsSclassCd} >= '50' AND #{gdsSclassCd} < '60' THEN '50' WHEN #{gdsSclassCd} >= '60' AND #{gdsSclassCd} < '70' THEN '60' ELSE '99' /* 홈 링크코드 */ END AS HOME_CD ,( SELECT /* 대분류명 조회 */ CONCAT(CD_NM,'렌탈') FROM TB_COM100 /* 공통코드 */ WHERE GRP_CD_ID = 'GDS_MCLASS_CD'
AND CD_ID = '10'
AND CD_ID = '20'
AND CD_ID = '30'
AND CD_ID = '40'
AND CD_ID = '50'
AND CD_ID = '60'
) AS GDS_MCLASS_NM /* 상품중분류명 */ ,( SELECT /* 소분류명 조회 */ CD_NM FROM TB_COM100 -- 공통코드 WHERE GRP_CD_ID = 'GDS_SCLASS_CD'
/* 상세페이지 PATH 경로 조회 */ AND CD_ID IN(SELECT GDS_SCLASS_CD FROM TB_GDS100 WHERE GDS_CD = #{gdsCd} )
AND CD_ID = #{gdsSclassCd}
) AS GDS_SCLASS_NM /* 상품소분류명 */ FROM TB_COM100

SELECT /* 브랜드명 조회 */
	DISTINCT BRAND_NM				/* 브랜드명 */
FROM TB_GDS100		/* 공통상품 */
WHERE 1=1

	
		
			AND GDS_MCLASS_CD = #{gdsSclassCd}
		 
		
			AND GDS_SCLASS_CD = #{gdsSclassCd}

SELECT /* 가격대 조회 */ TRUNCATE((AA.MIN * 1.3), 0) AS PRICE_MIN /* 최소가격 * 1.3배 */ ,TRUNCATE((AA.MAX * 0.7), 0) AS PRICE_MAX /* 최대가격 * 0.7배 */ FROM ( SELECT MIN(B.GDS_PRICE) AS MIN ,MAX(B.GDS_PRICE) AS MAX FROM TB_GDS100 A /* 공통상품 */ ,TB_GDS200 B /* 렌탈상품상세 */ WHERE A.GDS_CD = B.GDS_CD
AND A.GDS_MCLASS_CD = #{gdsSclassCd}
AND A.GDS_SCLASS_CD = #{gdsSclassCd}
) AA

SELECT AA.* FROM ( SELECT A.GDS_CD ,A.GDS_LCLASS_CD ,A.GDS_MCLASS_CD ,A.GDS_SCLASS_CD ,A.GDS_NM ,A.BRAND_NM ,A.MODEL_NM ,B.IMG1 ,A.REG_DT ,B.GDS_PRICE ,A.GDS_GB_CD ,CASE WHEN A.GDS_GB_CD = 'N' THEN '새상품' WHEN A.GDS_GB_CD = 'U' THEN '중고상품' ELSE '누락' END AS GDS_GB_NM FROM TB_GDS100 A /* 공통상품 */ ,TB_GDS200 B /* 렌탈상품상세 */ WHERE A.GDS_CD = B.GDS_CD /* 중분류냐 소분류냐 선택에 따라 */
AND A.GDS_MCLASS_CD = #{gdsSclassCd}
AND A.GDS_SCLASS_CD = #{gdsSclassCd}
/* 브랜드명 리스트 반복문 */
AND
A.BRAND_NM LIKE #{list}
/* 가격범위 */
AND B.GDS_PRICE >= #{searchPriceSt}
AND B.GDS_PRICE <= #{searchPriceEd}
/* 상품상태 */
AND A.GDS_GB_CD = #{sts}
/* 결과내검색 */
AND A.GDS_NM LIKE CONCAT('%', #{search},'%')
) AA
ORDER BY AA.REG_DT DESC
ORDER BY AA.GDS_PRICE ,AA.REG_DT DESC
ORDER BY AA.GDS_PRICE DESC ,AA.REG_DT DESC

SELECT AAA.* FROM ( SELECT BB.* ,COALESCE (AA.CNT, 0) AS CNT /* 대상건수 */
,COALESCE (AA.TOT_GRD_SUM, 0) AS TOT_GRD_SUM /* 토탈점수합계 */ ,COALESCE (AA.TOT_GRD_AVG, 0) AS TOT_GRD_AVG /* 토탈점수평균 */
FROM ( SELECT GDS_CD ,COUNT(A.GDS_CD) AS CNT
,SUM(A.GRADE) AS TOT_GRD_SUM ,ROUND(SUM(A.GRADE) / COUNT(A.GDS_CD),2) AS TOT_GRD_AVG FROM TB_GDS210 A /* 렌탈상품후기 */
FROM TB_ODR110 A /* 주문상세 */
GROUP BY A.GDS_CD ) AA RIGHT OUTER JOIN (SELECT /* */ A.GDS_CD ,A.GDS_LCLASS_CD ,A.GDS_MCLASS_CD ,A.GDS_SCLASS_CD ,A.GDS_NM ,A.BRAND_NM ,A.MODEL_NM ,B.IMG1 ,A.REG_DT ,B.GDS_PRICE ,A.GDS_GB_CD ,CASE WHEN A.GDS_GB_CD = 'N' THEN '새상품' WHEN A.GDS_GB_CD = 'U' THEN '중고상품' ELSE '누락' END AS GDS_GB_NM FROM TB_GDS100 A /* 공통상품 */ ,TB_GDS200 B /* 렌탈상품상세 */ WHERE A.GDS_CD = B.GDS_CD /* 중분류냐 소분류냐 선택에 따라 */
AND A.GDS_MCLASS_CD = #{gdsSclassCd}
AND A.GDS_SCLASS_CD = #{gdsSclassCd}
/* 브랜드명 리스트 반복문 */
AND
A.BRAND_NM LIKE #{list}
/* 가격범위 */
AND B.GDS_PRICE >= #{searchPriceSt}
AND B.GDS_PRICE <= #{searchPriceEd}
/* 상품상태 */
AND A.GDS_GB_CD = #{sts}
/* 결과내검색 */
AND A.GDS_NM LIKE CONCAT('%', #{search},'%')
) BB ON AA.GDS_CD = BB.GDS_CD ) AAA
ORDER BY AAA.TOT_GRD_AVG DESC ,AAA.CNT DESC ,AAA.REG_DT DESC
ORDER BY AAA.CNT DESC /* 판매량수 */ ,AAA.REG_DT DESC
ORDER BY AAA.CNT DESC ,AAA.REG_DT DESC

mapper.insert:
INSERT INTO
TB_QUEST100 (
		QUEST_NO
		,QUEST_DT
		,GDS_CD
		,QUEST_NM
		,HP
		,EMAIL
		,QUEST_STS_CD
) VALUES (
		(SELECT MAX(LPAD(SEQ,8,0)) FROM SEQ_QUEST100)
		,(SELECT DATE_FORMAT(NOW(),'%Y%m%d'))
		,#{gdsCd}
		,#{questNm}
		,#{hp}
		,#{email}
		,'1'
)
