mapper.select:
 
		SELECT *, FN_NM('GDS_MCLASS_NM',GDS_MCLASS_CD) AS mcnm
		FROM tb_used100
		WHERE USED_GDS_NO=#{usedGdsNo}

mapper.insert:
 
		 INSERT INTO tb_used100 (USED_GDS_NO,USED_GDS_DT,GDS_LCLASS_CD,GDS_MCLASS_CD,GDS_SCLASS_CD,GDS_GB_CD,BRAND_NM,MODEL_NM,SUB,USED_GDS_PRICE,CONTENT,IMG1,IMG2,IMG3,IMG4) 
		 SELECT MAX(LPAD(SEQ,8,0)),DATE_FORMAT(now(), '%Y%m%d'),#{gdsLclassCd},#{gdsMclassCd},#{gdsSclassCd},'U',#{brandNm},#{modelNm},#{sub},#{usedGdsPrice},#{content},#{img1},#{img2},#{img3},#{img4} 
		 FROM SEQ_USED100

INSERT INTO tb_used110 (USED_GDS_NO,USED_GDS_COMT_NO,USED_GDS_COMT_DT,MB_NO,CONTENT,SECRET_YN,USED_RE_GB_CD,DEL_YN)
SELECT #{usedGdsNo},MAX(USED_GDS_COMT_NO)+1, DATE_FORMAT(now(), '%Y%m%d'), #{mbNo},#{content},#{secretYn},#{usedReGbCd},#{delYn}
FROM tb_used110 WHERE USED_GDS_NO=#{usedGdsNo}
